    private function attachImageToMedia($model, $publicPath)
    {


        if (!file_exists($publicPath)) {
            return false;
        }

        $file = new \Illuminate\Http\File($publicPath);

        return $model->addMedia($file)->preservingOriginal()->toMediaCollection('image');
    }
